import React from "react";
import { Row, Col, Form, FormGroup, Input, Button, Toast, ToastBody, Card, CardBody,
	   Dropdown, DropdownToggle, DropdownMenu, DropdownItem, Container } from "reactstrap";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faUser } from "@fortawesome/free-solid-svg-icons";
import axios from "axios";
import moment from "moment";
import socketio from "socket.io-client";
import "./style.scss";

const socket = socketio();

class Chatting extends React.Component {

	constructor(props) {
		super(props);
		this.state = {
			message : '',			// 전송할 메시지(전체 채팅)
			chatList : [],			// 메시지 리스트(전체 채팅)
			userId : '',			// 유저 아이디
			channel : 'channel',	// 채널(전체 채팅)
			userList : [],			// 접속한 유저 리스트
			dropdownOpen : false	// 접속자 리스트 버튼
		}
	}

	componentDidUpdate() {
		const scroll = document.getElementsByClassName('scroll')[0];
		scroll.scrollTop = scroll.scrollHeight;
	}

	componentDidMount(){
		// 유저 아이디 정보 얻기
		axios.get('/api/account/id')
		.then(({ data }) => {
			this.setState({
				userId: data
			});

			this.props.propId(data);
			socket.emit('channel', this.state.userId);
		});

		let _this = this;
		// 전체 채팅 기록 얻기
		socket.on('receive', function(data) {
			_this.setState({ chatList : _this.state.chatList.concat([data]) });
		});

		// 전체 사용자 정보 얻기
		socket.on('receiveUserList', function(data) {
			_this.setState({ userList : data });
		});
	}

	// ****************************************************** // 
	// 사용자 인원 클릭
	// ****************************************************** //
	toggle = () => {
		this.setState({dropdownOpen : !this.state.dropdownOpen});
	}

	// ****************************************************** // 
	// 귓속말 대화창 열기
	// ****************************************************** //
	whisperOpen = (e) => {
		const receiverId = e.target.innerText;

		this.props.propWhisper(receiverId);
	}

	// ****************************************************** // 
	// 전체 채팅 메시지 input 내용 변경
	// ****************************************************** //
	messageChange = (e) => {
		this.setState({ message : e.target.value });
	}

	// ****************************************************** // 
	// 전체 채팅 메시지 input 엔터키
	// ****************************************************** //
	enterSend = (e) => {
        if(e.keyCode === 13) {
			if (!e.shiftKey){
            	e.preventDefault();
            	this.send();
			}
        }
    }

	// ****************************************************** // 
	// 전체 채팅 메시지 전송
	// ****************************************************** //
	send = () => {
		if(!this.state.message){
			alert("메시지를 입력하세요.");
		}
		else{
			socket.emit('send',{ 
			msg : this.state.message, 
			id : this.state.userId, 
			channel : this.state.channel });

			this.setState({ message : '' });
		}
	}

	render() {
		const { chatList, userList } = this.state;

		// ****************************************************** // 
		// 전체 채팅 메시지 리스트 얻기
		// 중복이니깐 어떻게 할 방법 없는가
		// ****************************************************** //
		const messages = chatList.map((msg, index) => {
			const replaceMsg = msg.chat.msg.split('\n').map( (content, index) => {
				return (
					<span key={index}>{content}<br/></span>
				)
			});

			const date = moment(msg.chat.date).format('YYYY-MM-DD A hh:mm');
			if(msg.chat.id === this.state.userId){
				return(
					<Row key={index} >
						<Col xs={{ size : 6, offset : 6}} sm={{ size : 6, offset : 6}} md={{ size : 6, offset : 6}} >
							<Toast className={style.Msg}>
								<ToastBody className={style.MyMsgContent}>
									{replaceMsg}
								</ToastBody>
							</Toast>
							<div className={style.MsgDate}>{date}</div>
						</Col>
					</Row>
				)
			}
			else {
				return(
					<Row key={index} >
						<Col xs={{ size : 6}} sm={{ size : 6 }} md={{ size : 6 }}>
							<div>
								<span onClick={this.whisperOpen} className={style.MsgUserId}>{msg.chat.id}</span>
							</div>
							<Toast className={style.Msg}>
								<ToastBody className={style.MsgContent}>
									{replaceMsg}
								</ToastBody>
							</Toast>
							<div>
								<div></div>
							</div>
							<div className={style.MsgDate}>{date}</div>
						</Col>
					</Row>
				)
			}
		});	

		// ****************************************************** // 
		// 전체 채널 접속자 수
		// ****************************************************** //
		const users = userList.map((user, index) => {
			return (
				<DropdownItem key={index} onClick={this.whisperOpen}>{user.id}</DropdownItem>
			)
		});

		return (
			<div>
				<Container>
					<Col sm="12" md={{ size: 6, offset: 3 }} >
						<Card className={style.Chat} >
							<CardBody className="ChatTitle" >
								<div>전체 채팅</div>
								<Dropdown isOpen={this.state.dropdownOpen} toggle={this.toggle}>
									<DropdownToggle color="link" className={style.UserId}>
										<FontAwesomeIcon icon={faUser} className="icon-size" /> <span>{userList.length}</span>
									</DropdownToggle>
									<DropdownMenu modifiers={{ setMaxHeight: {enabled: true, order: 890, fn: (data) => { return { ...data, styles: { ...data.styles, overflow: 'auto', maxHeight: '100px', }, };},},}} >
									{users}
								</DropdownMenu>
								</Dropdown>
							</CardBody>
							<CardBody className={[style.ChatBody, "scroll"]}>
								{messages}
							</CardBody>
							<CardBody className={style.ChatBtm}>
								<Form>
									<FormGroup>
										<Input type="textarea" placeholder="메시지를 입력해주세요." onChange={this.messageChange} onKeyDown={this.enterSend} value={this.state.message} />
									</FormGroup>
									<Button color="primary" onClick={this.send}>전송</Button>
								</Form>
							</CardBody>
						</Card>
					</Col>
				</Container>
			</div>
		);
	}
}

export default Chatting;
