import React from "react";
import { Link } from "react-router-dom";
import { Button, Form, FormGroup, Label, Input, Container, Row, Col } from "reactstrap";
import style from "./style.scss";
import axios from "axios";

const getErrorMsg = errCode => {
  let errorMsg;
  switch (errCode) {
    case 400:
      errorMsg = "잘못된 요청입니다.";
      break;
    case 420:
      errorMsg = "잘못된 아이디나 패스워드입니다.";
      break;
    default:
      errorMsg = "잠시 후 다시 시도해주시기랍니다.";
      break;
  }
  return errorMsg;
};

class Signin extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      id: "",
      pw: ""
    };
  }

  onChangeId = e => {
    this.setState({ id: e.currentTarget.value });
  };

  onChangePw = e => {
    this.setState({ pw: e.currentTarget.value });
  };

  signIn = () => {
    const { id, pw } = this.state;
    // axios
    //   .post("/api/account/signin", { id, pw })
    //   .then(({ data }) => {
    //     window.location.href = "/";
    //   })
    //   .catch(err => {
    //     alert(getErrorMsg(err.response.status));
    //   });
  };

  render() {
    const { id, pw } = this.state;
    return (
      <div className="Signin">
        <Container>
          <Row className="padding-top-btm-1">
            <Col>
              <span className="pageTitle">로그인</span>
            </Col>
          </Row>
          <Row className="padding-top-btm-1">
            <Col xs={{size : 8, offset : 2}} sm={{size : 8, offset : 2}} md={{size : 5, offset : 3}}>
              <Input
                type="text"
                name="id"
                id="inputId"
                placeholder="아이디"
                onChange={this.onChangeId}
              />
            </Col>
          </Row>
          <Row className="padding-top-btm-1">
            <Col xs={{size : 8, offset : 2}} sm={{size : 8, offset : 2}} md={{size : 5, offset : 3}}>
              <Input
                type="password"
                name="password"
                id="inputPW"
                placeholder="비밀번호"
                onChange={this.onChangePw}
              />
            </Col>
          </Row>
          <Row className="padding-top-btm-1">
            <Col md={{size : 4, offset : 4}}>
              <Button color="primary" onClick={this.signIn}>
                <span className="font-bold-700">로그인</span>
              </Button>
              <Link to="/signup" className="signupBtn">
                <Button color="success">
                  <span className="font-bold-700">회원가입</span>
                </Button>
              </Link>
            </Col>
          </Row>
        </Container>
      </div>
    );
  }
}

export default Signin;
