import React from "react";
import { Row, Col, Toast, ToastBody } from "reactstrap";
import "./style.scss";

const ChatListGroup = ({ chatList }) => (
	<React.Fragment>
		{
            chatList.map((msg, index) => {
                const replaceMsg = msg.chat.msg.split('\n').map( (content, index) => {
                    return (
                        <span key={index}>{content}<br/></span>
                    )
                });

                const date = moment(msg.chat.date).format('YYYY-MM-DD A hh:mm');
                if(msg.chat.id === this.state.userId){
                    return(
                        <Row key={index} >
                            <Col xs={{ size : 6, offset : 6}} sm={{ size : 6, offset : 6}} md={{ size : 6, offset : 6}} >
                                <Toast className="Msg">
                                    <ToastBody className="MyMsgContent">
                                        {replaceMsg}
                                    </ToastBody>
                                </Toast>
                                <div className="MsgDate">{date}</div>
                            </Col>
                        </Row>
                    )
                }
                else {
                    return(
                        <Row key={index} >
                            <Col xs={{ size : 6}} sm={{ size : 6 }} md={{ size : 6 }}>
                                <div>
                                    <span onClick={this.whisperOpen} className="MsgUserId">{msg.chat.id}</span>
                                </div>
                                <Toast className="Msg">
                                    <ToastBody className="MsgContent">
                                        {replaceMsg}
                                    </ToastBody>
                                </Toast>
                                <div>
                                    <div></div>
                                </div>
                                <div className="MsgDate">{date}</div>
                            </Col>
                        </Row>
                    )
                }
            })
        }
	</React.Fragment>
);

export default ChatListGroup;
