import React from "react";
import axios from "axios";
import { Button, Form, FormGroup, Label, Input, FormText } from "reactstrap";
import { Link } from "react-router-dom";

import style from "./style.scss";

const getErrorMsg = errCode => {
  let errorMsg;
  switch (errCode) {
    case 400:
      errorMsg = "잘못된 요청입니다.";
      break;
    case 420:
      errorMsg = "잘못된 아이디나 패스워드입니다.";
      break;
    default:
      errorMsg = "잠시 후 다시 시도해주시기랍니다.";
      break;
  }
  return errorMsg;
};

class Signin extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      id: "",
      pw: ""
    };
  }

  onChangeId = e => {
    this.setState({ id: e.currentTarget.value });
  };

  onChangePw = e => {
    this.setState({ pw: e.currentTarget.value });
  };

  signIn = () => {
    const { id, pw } = this.state;
    axios
      .post("/api/account/signin", { id, pw })
      .then(({ data }) => {
        window.location.href = "/";
      })
      .catch(err => {
        alert(getErrorMsg(err.response.status));
      });
  };

  render() {
    const { id, pw } = this.state;
    return (
      <div>
        <div>
          <span>signin page</span>
        </div>
        <div>
          <Form>
            <FormGroup>
              <Label for="inputId">ID</Label>
              <Input
                type="text"
                name="id"
                id="inputId"
                placeholder="id"
                onChange={this.onChangeId}
              />
            </FormGroup>
            <FormGroup>
              <Label for="inputPW">PASSWORD</Label>
              <Input
                type="password"
                name="password"
                id="inputPW"
                placeholder="password"
                onChange={this.onChangePw}
              />
            </FormGroup>
            <Button color="primary" onClick={this.signIn}>
              로그인
            </Button>
          </Form>
          <Link to="/signup">
            <Button color="success">회원가입</Button>
          </Link>
        </div>
      </div>
    );
  }
}

export default Signin;
