import React from "react";
import { Dropdown, DropdownToggle, DropdownMenu, DropdownItem, Button } from "reactstrap";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faUser } from "@fortawesome/free-solid-svg-icons";
import "./style.scss";

const ExitBtn = (receiver) => {
    if(receiver){
        return (
            <Button className="exitBtn" color="secondary" size="sm"><span>나가기</span></Button>
        )
    }
}

const UserList = (receiver) => {
    if(!receiver){
        UserList = (
            <Dropdown isOpen={dropdownOpen} toggle={toggle}>
                <DropdownToggle color="link" className="userId">
                    <FontAwesomeIcon icon={faUser} className="icon-size" /> <span>{userList.length}</span>
                </DropdownToggle>
                <DropdownMenu modifiers={{ setMaxHeight: {enabled: true, order: 890, fn: (data) => { return { ...data, styles: { ...data.styles, overflow: 'auto', maxHeight: '100px', }, };},},}} >
                {
                    userList.map((user, index) => {
                        return (
                            <DropdownItem key={index} onClick={whisperOpen}>{user.id}</DropdownItem>
                        )
                    })
                }
                </DropdownMenu>
            </Dropdown>
        );

        ChatName = (
            <span>전체 채팅</span>
        );
    }
    else {
        UserList = (
            <span className="receiver">{receiver}</span>
        );

        ChatName = (
            <span>귓속말</span>
        );
    }
}

const ChatTitleGroup = ({ userList, dropdownOpen, toggle, whisperOpen, receiver }) => {
    let UserList = "";
    let ChatName = "";



    return (
        <React.Fragment>
            <div className="chatName">
                {ChatName}
                {ExitBtn(receiver)}
            </div>
            {UserList(receiver)}
        </React.Fragment>
    );
}

export default ChatTitleGroup;
