import React from "react";
import { Input, FormText } from "reactstrap";
import axios from "axios";

import UserInfoInputGroup from "../../components/UserInfoInputGroup";
import CenterLayout from "../../components/CenterLayout";
import UserButtonGroup from "../../components/UserButtonGroup";

import style from "./style.scss";

const getErrorMsg = errCode => {
  let errorMsg;
  switch (errCode) {
    case 400:
      errorMsg = "잘못된 요청입니다.";
      break;
    case 419:
      errorMsg = "이미 존재하는 아이디입니다.";
      break;
    default:
      errorMsg = "잠시 후 다시 시도해주시기랍니다.";
      break;
  }
  return errorMsg;
};

class Signup extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      id: "",
      pw: "",
      repw: ""
    };
  }

  onChangeId = e => {
    this.setState({ id: e.currentTarget.value });
  };

  onChangePw = e => {
    this.setState({ pw: e.currentTarget.value });
  };

  onChangeRePw = e => {
    this.setState({ repw: e.currentTarget.value });
  };

  signUp = () => {
    const { id, pw, repw } = this.state;
    if (pw === repw) {
      axios
        .post("/api/account/signup", {
          id,
          pw
        })
        .then(({ data }) => {
          window.location.href = "/signin";
        })
        .catch(err => {
          alert(getErrorMsg(err.response.status));
        });
    } else {
      alert("비밀번호와 비밀번호 확인이 같지 않습니다.");
    }
  };

  render() {
    const { id, pw, repw, errorMsg } = this.state;
    return (
      <div>
        <span>signup page</span>
      </div>
    );
  }
}

export default Signup;
