import React from "react";
import { Form, FormGroup, Input, Button } from "reactstrap";
import "./style.scss";

const ChatBtmGroup = ({ messageChange, enterSend, message, send }) => (
	<React.Fragment>
        <Form>
            <FormGroup>
            <InputGroup>
            <Input type="textarea" placeholder="메시지를 입력해주세요." onChange={messageChange} onKeyDown={enterSend} value={message} className="inputMsg" />
                <InputGroupAddon addonType="append">
                    <InputGroupText>@example.com</InputGroupText>
                </InputGroupAddon>
                </InputGroup>

            </FormGroup>
            <Button color="primary" onClick={send}>전송</Button>
        </Form>
	</React.Fragment>
);

export default ChatBtmGroup;
