import React from "react";
import { Col, Card, CardBody, Container } from "reactstrap";
import axios from "axios";
import socketio from "socket.io-client";
import ChatTitleGroup from "../../../../components/chatTitleGroup";
import ChatListGroup from "../../../../components/chatListGroup";
import ChatBtmGroup from "../../../../components/chatBtmGroup";
import "./style.scss";

const socket = socketio();

class Chatting extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			message : '',			// 전송할 메시지(전체 채팅)
			chatList : [],			// 메시지 리스트(전체 채팅)
			userId : '',			// 유저 아이디
			channel : 'channel',	// 채널(전체 채팅)
			userList : [],			// 접속한 유저 리스트
			dropdownOpen : false	// 접속자 리스트 버튼
		}
	}

	componentDidMount(){
		// 유저 아이디 정보 얻기
		axios.get('/api/users/id')
		.then(({ data }) => {
			if(data){
                this.setState({ userId : data });
            }
            else{
                window.location.href = "/";
            }

			// this.props.propId(data);
			socket.emit('channel', this.state.userId);
		});

		let _this = this;
		// 전체 채팅 기록 얻기
		socket.on('receive', (data) => {
			this.setState({ chatList : this.state.chatList.concat([data]) });

			const scroll = this.scroll;
			scroll.scrollTop = scroll.scrollHeight;
		});

		// 전체 사용자 정보 얻기
		socket.on('receiveUserList', (data) => {
			_this.setState({ userList : data });
		});
	}

	// ****************************************************** // 
	// 사용자 인원 클릭
	// ****************************************************** //
	toggle = () => {
		this.setState({dropdownOpen : !this.state.dropdownOpen});
	}

	// ****************************************************** // 
	// 귓속말 대화창 열기
	// ****************************************************** //
	whisperOpen = (e) => {
		// const receiverId = e.target.innerText;

		// this.props.propWhisper(receiverId);
	}

	// ****************************************************** // 
	// 전체 채팅 메시지 input 내용 변경
	// ****************************************************** //
	messageChange = (e) => {
		this.setState({ message : e.target.value });
	}

	// ****************************************************** // 
	// 전체 채팅 메시지 input 엔터키
	// ****************************************************** //
	enterSend = (e) => {
        if(e.keyCode === 13) {
			if (!e.shiftKey){
            	e.preventDefault();
            	this.send();
			}
        }
    }

	// ****************************************************** // 
	// 전체 채팅 메시지 전송
	// ****************************************************** //
	send = () => {
		if(!this.state.message){
			alert("메시지를 입력하세요.");
		}
		else{
			socket.emit('send',{ msg : this.state.message, id : this.state.userId, channel : this.state.channel });
			this.setState({ message : '' });
		}
	}

	render() {
		const { chatList, userList } = this.state;

		return (
			<div>
				<Container>
					<Col xs={{size : 10, offset : 1}} sm={{size : 10, offset : 1}} md={{ size: 6, offset: 3 }} >
						<Card className="chat" >
							<CardBody className="chatTitle" >
								<ChatTitleGroup userList={userList} dropdownOpen={this.state.dropdownOpen} toggle={this.toggle} whisperOpen={this.whisperOpen}/>
							</CardBody>
							<CardBody className="chatBody" ref={ref => { this.scroll = ref; }}>
								<ChatListGroup chatList={chatList}/>
							</CardBody>
							<CardBody className="chatBtm">
								<ChatBtmGroup messageChange={this.messageChange} enterSend={this.enterSend} message={this.state.message}/>
							</CardBody>
						</Card>
					</Col>
				</Container>
			</div>
		);
	}
}

export default Chatting;
